mapper.update:
UPDATE route
SET name                  = #{name}
AND distance              = #{distance}
AND seat_residual         = #{seatResidual}
AND grade                 = #{grade}
AND departure_terminal_id = #{departureTerminalId}
AND arrive_terminal_id    = #{arriveTerminalId}
AND departure_time        = {departureTime}
AND arrive_time           = #{arriveTime}
AND updated_at            = NOW()
AND status                = #{status}
WHERE id                  = #{id}
